mapper.select:
select 

from tbl_qpbat_bat_task_st
where BUSS_CD = #{bussCd,jdbcType=VARCHAR}
  and SETTLE_DT = #{settleDt,jdbcType=VARCHAR}
  and TASK_ID = #{taskId,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_qpbat_bat_task_st
where BUSS_CD = #{bussCd,jdbcType=VARCHAR}
  and SETTLE_DT = #{settleDt,jdbcType=VARCHAR}
  and TASK_ID = #{taskId,jdbcType=VARCHAR}
